import React, { useState, useRef, useEffect } from 'react';
import './AttendanceModal.css';

const AttendanceModal = ({ onClose }) => {
  const [step, setStep] = useState('instructions');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [cameraStatus, setCameraStatus] = useState('Ready to start');
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Clean up camera on unmount
  useEffect(() => {
    return () => {
      closeCamera();
    };
  }, []);

  const closeCamera = () => {
    if (streamRef.current) {
      console.log('üõë Stopping camera stream');
      streamRef.current.getTracks().forEach(track => {
        track.stop();
      });
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const startCamera = async () => {
    try {
      console.log('üöÄ Starting camera...');
      setLoading(true);
      setCameraStatus('Requesting camera access...');

      // Stop any existing stream first
      closeCamera();

      // Try different constraints
      const constraints = {
        video: {
          width: { min: 640, ideal: 1280, max: 1920 },
          height: { min: 480, ideal: 720, max: 1080 },
          facingMode: 'user',
          frameRate: { ideal: 30 }
        }
      };

      setCameraStatus('Accessing camera...');
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      
      console.log('‚úÖ Camera stream obtained');
      setCameraStatus('Camera accessed, initializing video...');

      if (videoRef.current) {
        const video = videoRef.current;
        video.srcObject = stream;
        streamRef.current = stream;

        // Wait for video to be ready
        await new Promise((resolve, reject) => {
          let resolved = false;

          const onCanPlay = () => {
            if (!resolved) {
              console.log('üé¨ Video can play');
              resolved = true;
              video.removeEventListener('canplay', onCanPlay);
              video.removeEventListener('error', onError);
              resolve();
            }
          };

          const onError = (error) => {
            if (!resolved) {
              console.error('‚ùå Video error:', error);
              resolved = true;
              video.removeEventListener('canplay', onCanPlay);
              video.removeEventListener('error', onError);
              reject(error);
            }
          };

          video.addEventListener('canplay', onCanPlay);
          video.addEventListener('error', onError);

          // Force play
          video.play().then(() => {
            console.log('‚ñ∂Ô∏è Video play() successful');
          }).catch(playError => {
            console.warn('‚ö†Ô∏è Video play() failed:', playError);
          });

          // Fallback timeout
          setTimeout(() => {
            if (!resolved) {
              console.log('‚è∞ Video load timeout, proceeding...');
              resolved = true;
              video.removeEventListener('canplay', onCanPlay);
              video.removeEventListener('error', onError);
              resolve();
            }
          }, 2000);
        });

        console.log('üìπ Video dimensions:', video.videoWidth, 'x', video.videoHeight);
        setCameraStatus(`Camera ready! ${video.videoWidth}x${video.videoHeight}`);
      }

      setStep('camera');
      
    } catch (error) {
      console.error('‚ùå Camera failed:', error);
      setCameraStatus(`Camera error: ${error.message}`);
      
      // Try fallback with minimal constraints
      try {
        console.log('üîÑ Trying fallback camera...');
        setCameraStatus('Trying fallback camera...');
        
        const fallbackStream = await navigator.mediaDevices.getUserMedia({
          video: true // Minimal constraints
        });
        
        if (videoRef.current) {
          videoRef.current.srcObject = fallbackStream;
          streamRef.current = fallbackStream;
          setStep('camera');
          setCameraStatus('Camera ready (fallback mode)');
        }
      } catch (fallbackError) {
        console.error('‚ùå Fallback camera failed:', fallbackError);
        setCameraStatus(`Camera unavailable: ${fallbackError.message}`);
        alert(`Cannot access camera: ${fallbackError.message}\n\nPlease check:\n1. Camera permissions are allowed\n2. No other app is using the camera\n3. Camera is connected and working`);
      }
    } finally {
      setLoading(false);
    }
  };

  const captureAndRecognize = async () => {
    setLoading(true);
    setCameraStatus('Capturing image...');

    const video = videoRef.current;
    const canvas = canvasRef.current;

    if (!video || !canvas) {
      alert('Camera not initialized. Please try again.');
      setLoading(false);
      return;
    }

    // Check if video is actually playing
    if (video.videoWidth === 0 || video.videoHeight === 0) {
      alert('Camera not ready. Please wait for camera to initialize.');
      setLoading(false);
      return;
    }

    try {
      const context = canvas.getContext('2d');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw current video frame to canvas
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      setCameraStatus('Processing face recognition...');

      // Convert to blob and send to backend
      canvas.toBlob(async (blob) => {
        if (!blob) {
          alert('Failed to capture image. Please try again.');
          setLoading(false);
          return;
        }

        const formData = new FormData();
        formData.append('image', blob, 'face_capture.jpg');

        try {
          const token = localStorage.getItem('token');
          const response = await fetch('http://localhost:5000/api/student/mark-attendance', {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${token}`,
            },
            body: formData
          });

          const result = await response.json();

          if (result.success) {
            setResult(result);
            setStep('result');
            setCameraStatus('Attendance marked successfully!');
          } else {
            alert(result.error || 'Recognition failed');
            setCameraStatus('Recognition failed');
          }
        } catch (error) {
          alert('Recognition failed: ' + error.message);
          setCameraStatus('Network error');
        } finally {
          setLoading(false);
        }
      }, 'image/jpeg', 0.9);

    } catch (error) {
      console.error('Capture error:', error);
      alert('Failed to capture image: ' + error.message);
      setLoading(false);
    }
  };

  const handleClose = () => {
    closeCamera();
    onClose();
  };

  const handleBackToInstructions = () => {
    closeCamera();
    setStep('instructions');
    setCameraStatus('Ready to start');
  };

  // Test function to check if video is working
  const testVideoElement = () => {
    if (videoRef.current) {
      const video = videoRef.current;
      console.log('üîç Video element test:');
      console.log('- srcObject:', video.srcObject);
      console.log('- videoWidth:', video.videoWidth);
      console.log('- videoHeight:', video.videoHeight);
      console.log('- readyState:', video.readyState);
      console.log('- paused:', video.paused);
      console.log('- currentTime:', video.currentTime);
      
      if (video.videoWidth > 0 && video.videoHeight > 0) {
        setCameraStatus(`‚úÖ Video working: ${video.videoWidth}x${video.videoHeight}`);
      } else {
        setCameraStatus('‚ùå Video not displaying');
      }
    }
  };

  return (
    <div className="modal-overlay">
      <div className="attendance-modal">
        {/* Header */}
        <div className="modal-header">
          <h2>
            {step === 'instructions' && 'üì∑ Mark Attendance'}
            {step === 'camera' && 'üéØ Face Recognition'}
            {step === 'result' && '‚úÖ Attendance Marked'}
          </h2>
          <button className="close-btn" onClick={handleClose}>√ó</button>
        </div>

        {/* Instructions Step */}
        {step === 'instructions' && (
          <div className="modal-content">
            <div className="instructions">
              <div className="instruction-item">
                <div className="instruction-icon">üîí</div>
                <div className="instruction-text">
                  <h3>Allow Camera Access</h3>
                  <p>Click "Allow" when browser asks for camera permission</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">üí°</div>
                <div className="instruction-text">
                  <h3>Good Lighting</h3>
                  <p>Face a light source for better recognition</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">üë§</div>
                <div className="instruction-text">
                  <h3>Position Face</h3>
                  <p>Keep your face centered in the circle</p>
                </div>
              </div>
            </div>

            <div className="camera-status-info">
              <p>Status: {cameraStatus}</p>
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={handleClose}>
                Cancel
              </button>
              <button 
                className="btn primary" 
                onClick={startCamera}
                disabled={loading}
              >
                {loading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    Starting Camera...
                  </div>
                ) : (
                  'Start Camera'
                )}
              </button>
            </div>
          </div>
        )}

        {/* Camera Step */}
        {step === 'camera' && (
          <div className="modal-content">
            <div className="camera-container">
              <video 
                ref={videoRef} 
                autoPlay 
                playsInline
                muted
                className="camera-video"
                onLoadStart={() => console.log('üìπ Video load start')}
                onLoadedData={() => console.log('üìπ Video data loaded')}
                onLoadedMetadata={() => console.log('üìπ Video metadata loaded')}
                onCanPlay={() => console.log('üé¨ Video can play')}
                onPlay={() => console.log('‚ñ∂Ô∏è Video playing')}
                onError={(e) => console.error('‚ùå Video error event:', e)}
              />
              <div className="camera-overlay">
                <div className="face-guide"></div>
              </div>
              
              {/* Debug button - remove in production */}
              <button 
                className="debug-btn"
                onClick={testVideoElement}
                title="Test video element"
              >
                üîç
              </button>
            </div>
            <canvas ref={canvasRef} style={{ display: 'none' }} />

            <div className="camera-instructions">
              <p>Position your face within the frame and click capture</p>
              <div className="camera-status-info">
                <p>{cameraStatus}</p>
                {videoRef.current && videoRef.current.videoWidth > 0 && (
                  <p className="camera-resolution">
                    Resolution: {videoRef.current.videoWidth}√ó{videoRef.current.videoHeight}
                  </p>
                )}
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={handleBackToInstructions}>
                Back
              </button>
              <button 
                className="btn primary capture-btn"
                onClick={captureAndRecognize}
                disabled={loading || !videoRef.current || videoRef.current.videoWidth === 0}
              >
                {loading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    Processing...
                  </div>
                ) : (
                  'üì∏ Capture & Recognize'
                )}
              </button>
            </div>
          </div>
        )}

        {/* Result Step */}
        {step === 'result' && result && (
          <div className="modal-content">
            <div className="result-success">
              <div className="success-animation">
                <div className="checkmark">‚úì</div>
              </div>
              
              <h3>Attendance Marked Successfully!</h3>
              
              <div className="attendance-details">
                <div className="detail-item">
                  <span className="label">Student:</span>
                  <span className="value">{result.student.name}</span>
                </div>
                <div className="detail-item">
                  <span className="label">ID:</span>
                  <span className="value">{result.student.student_id}</span>
                </div>
                <div className="detail-item">
                  <span className="label">Confidence:</span>
                  <span className="value confidence">
                    {(result.confidence * 100).toFixed(1)}%
                  </span>
                </div>
                <div className="detail-item">
                  <span className="label">Time:</span>
                  <span className="value">{result.timestamp}</span>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn primary" onClick={handleClose}>
                Done
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceModal;
