import React, { useState, useRef, useEffect } from 'react';
import './AttendanceModal.css';

const AttendanceModal = ({ onClose }) => {
  const [step, setStep] = useState('instructions');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [cameraError, setCameraError] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Clean up camera on unmount
  useEffect(() => {
    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const startCamera = async () => {
    try {
      console.log('Starting camera...');
      setLoading(true);
      setCameraError(null);
      
      // Stop any existing stream first
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }

      // Try to get camera access
      const constraints = {
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          facingMode: 'user',
          frameRate: { ideal: 30 }
        }
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      
      console.log('Camera stream obtained:', stream);
      console.log('Video tracks:', stream.getVideoTracks());
      
      if (videoRef.current) {
        console.log('üé• Setting video srcObject');
        videoRef.current.srcObject = stream;
        streamRef.current = stream;

        // Set up event listeners for video element
        const video = videoRef.current;

        // Wait for video to be ready to play
        await new Promise((resolve, reject) => {
          const onCanPlay = () => {
            console.log('‚úÖ Video can play - ready to show');
            console.log('Video dimensions:', video.videoWidth, 'x', video.videoHeight);
            video.removeEventListener('canplay', onCanPlay);
            video.removeEventListener('error', onError);
            resolve();
          };

          const onError = (error) => {
            console.error('‚ùå Video element error:', error);
            video.removeEventListener('canplay', onCanPlay);
            video.removeEventListener('error', onError);
            reject(new Error('Video element failed to load'));
          };

          video.addEventListener('canplay', onCanPlay);
          video.addEventListener('error', onError);

          // Fallback timeout
          setTimeout(() => {
            console.log('‚è∞ Video load timeout - proceeding anyway');
            video.removeEventListener('canplay', onCanPlay);
            video.removeEventListener('error', onError);
            resolve();
          }, 3000);
        });

        // Play the video explicitly
        console.log('üé¨ Attempting to play video');
        await video.play();
        console.log('üé¨ Video playback started successfully');
      }
      
      console.log('‚úÖ Camera fully initialized');
      setStep('camera');
      
    } catch (error) {
      console.error('‚ùå Camera initialization failed:', error);
      setCameraError(error.message);
      
      // Try fallback constraints if main one fails
      if (error.name === 'OverconstrainedError' || error.name === 'ConstraintNotSatisfiedError') {
        try {
          console.log('üîÑ Trying fallback camera constraints...');
          const fallbackStream = await navigator.mediaDevices.getUserMedia({
            video: { facingMode: 'user' }
          });
          
          if (videoRef.current) {
            videoRef.current.srcObject = fallbackStream;
            streamRef.current = fallbackStream;
            setStep('camera');
          }
        } catch (fallbackError) {
          console.error('‚ùå Fallback camera also failed:', fallbackError);
          alert(`Camera Error: ${fallbackError.message}\n\nPlease ensure your camera is connected and permissions are granted.`);
        }
      } else {
        alert(`Camera Access Denied: ${error.message}\n\nPlease allow camera permissions in your browser settings and try again.`);
      }
    } finally {
      setLoading(false);
    }
  };

  const captureAndRecognize = async () => {
    setLoading(true);

    const video = videoRef.current;
    const canvas = canvasRef.current;

    if (!video || !canvas) {
      alert('Camera not initialized. Please try again.');
      setLoading(false);
      return;
    }

    // Ensure video has loaded and has valid dimensions
    if (video.videoWidth === 0 || video.videoHeight === 0) {
      alert('Video not ready. Please wait for camera to load.');
      setLoading(false);
      return;
    }

    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Clear canvas and draw video frame
    context.clearRect(0, 0, canvas.width, canvas.height);
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convert to blob and send to backend
    canvas.toBlob(async (blob) => {
      if (!blob) {
        alert('Failed to capture image. Please try again.');
        setLoading(false);
        return;
      }

      const formData = new FormData();
      formData.append('image', blob, 'face_capture.jpg');

      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/student/mark-attendance', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
          body: formData
        });

        const result = await response.json();

        if (result.success) {
          setResult(result);
          setStep('result');
        } else {
          alert(result.error || 'Recognition failed');
        }
      } catch (error) {
        alert('Recognition failed: ' + error.message);
      } finally {
        setLoading(false);
      }
    }, 'image/jpeg', 0.9);
  };

  const closeCamera = () => {
    if (streamRef.current) {
      console.log('üõë Stopping camera stream');
      streamRef.current.getTracks().forEach(track => {
        track.stop();
        console.log('Stopped track:', track.kind);
      });
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const handleClose = () => {
    closeCamera();
    onClose();
  };

  const handleBackToInstructions = () => {
    closeCamera();
    setStep('instructions');
    setCameraError(null);
  };

  // Debug component to show camera status
  const CameraDebugInfo = () => {
    if (!videoRef.current) return null;
    
    const video = videoRef.current;
    return (
      <div className="camera-debug">
        Size: {video.videoWidth}x{video.videoHeight} | 
        Ready: {video.readyState} | 
        Playing: {!video.paused ? 'Yes' : 'No'}
      </div>
    );
  };

  return (
    <div className="modal-overlay">
      <div className="attendance-modal">
        {/* Header */}
        <div className="modal-header">
          <h2>
            {step === 'instructions' && 'üì∑ Mark Attendance'}
            {step === 'camera' && 'üéØ Face Recognition'}
            {step === 'result' && '‚úÖ Attendance Marked'}
          </h2>
          <button className="close-btn" onClick={handleClose}>√ó</button>
        </div>

        {/* Instructions Step */}
        {step === 'instructions' && (
          <div className="modal-content">
            {cameraError && (
              <div className="error-message">
                <strong>Camera Error:</strong> {cameraError}
              </div>
            )}
            
            <div className="instructions">
              <div className="instruction-item">
                <div className="instruction-icon">1Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Position Yourself</h3>
                  <p>Find a well-lit area and face the camera directly</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">2Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Remove Accessories</h3>
                  <p>Take off hats, sunglasses for better recognition</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">3Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Stay Still</h3>
                  <p>Keep your face steady while we capture the image</p>
                </div>
              </div>

              <div className="instruction-item">
                <div className="instruction-icon">üîí</div>
                <div className="instruction-text">
                  <h3>Camera Permissions</h3>
                  <p>Make sure to allow camera access when prompted</p>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={handleClose}>
                Cancel
              </button>
              <button 
                className="btn primary" 
                onClick={startCamera}
                disabled={loading}
              >
                {loading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    Starting Camera...
                  </div>
                ) : (
                  'Start Camera'
                )}
              </button>
            </div>
          </div>
        )}

        {/* Camera Step */}
        {step === 'camera' && (
          <div className="modal-content">
            <div className="camera-container">
              {loading ? (
                <div className="camera-loading">
                  <div className="loading-spinner"></div>
                  <p>Initializing camera...</p>
                </div>
              ) : (
                <>
                  <video
                    ref={videoRef}
                    autoPlay
                    playsInline
                    muted
                    className="camera-video"
                    onLoadedMetadata={() => {
                      console.log('üìπ Video metadata loaded:', videoRef.current.videoWidth, 'x', videoRef.current.videoHeight);
                      console.log('Video readyState:', videoRef.current.readyState);
                    }}
                    onCanPlay={() => {
                      console.log('‚ñ∂Ô∏è Video can play');
                      console.log('Video dimensions:', videoRef.current.videoWidth, 'x', videoRef.current.videoHeight);
                    }}
                    onPlay={() => {
                      console.log('üé¨ Video started playing');
                      console.log('Video srcObject:', videoRef.current.srcObject);
                    }}
                    onError={(e) => {
                      console.error('‚ùå Video error:', e);
                      console.error('Video error details:', e.target.error);
                    }}
                    onLoadStart={() => console.log('üì∫ Video load started')}
                    onLoadedData={() => console.log('üìä Video data loaded')}
                    onWaiting={() => console.log('‚è≥ Video waiting')}
                    onStalled={() => console.log('üö´ Video stalled')}
                  />
                  <div className="camera-overlay">
                    <div className="face-guide"></div>
                  </div>
                  <CameraDebugInfo />
                </>
              )}
              <canvas ref={canvasRef} style={{ display: 'none' }} />
            </div>

            <div className="camera-instructions">
              <p>Position your face within the frame and click capture</p>
              {loading && <p className="loading-text">Processing your camera feed...</p>}
              
              {videoRef.current && videoRef.current.videoWidth > 0 && (
                <p className="camera-status">
                  ‚úÖ Camera active - {videoRef.current.videoWidth}x{videoRef.current.videoHeight}
                </p>
              )}
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={handleBackToInstructions}>
                Back
              </button>
              <button 
                className="btn primary capture-btn"
                onClick={captureAndRecognize}
                disabled={loading || !videoRef.current || videoRef.current.videoWidth === 0}
              >
                {loading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    Processing...
                  </div>
                ) : (
                  'üì∏ Capture & Recognize'
                )}
              </button>
            </div>
          </div>
        )}

        {/* Result Step */}
        {step === 'result' && result && (
          <div className="modal-content">
            <div className="result-success">
              <div className="success-animation">
                <div className="checkmark">‚úì</div>
              </div>
              
              <h3>Attendance Marked Successfully!</h3>
              
              <div className="attendance-details">
                <div className="detail-item">
                  <span className="label">Student:</span>
                  <span className="value">{result.student.name}</span>
                </div>
                <div className="detail-item">
                  <span className="label">ID:</span>
                  <span className="value">{result.student.student_id}</span>
                </div>
                <div className="detail-item">
                  <span className="label">Confidence:</span>
                  <span className="value confidence">
                    {(result.confidence * 100).toFixed(1)}%
                  </span>
                </div>
                <div className="detail-item">
                  <span className="label">Time:</span>
                  <span className="value">{result.timestamp}</span>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn primary" onClick={handleClose}>
                Done
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceModal;
