import React, { useState, useRef } from 'react';
import './AttendanceModal.css';

const AttendanceModal = ({ onClose }) => {
  const [step, setStep] = useState('instructions');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 640, 
          height: 480,
          facingMode: 'user'
        } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
      }
      setStep('camera');
    } catch (error) {
      console.error('Camera error:', error);
      alert('Camera access denied. Please allow camera permissions.');
    }
  };

  const captureAndRecognize = async () => {
    setLoading(true);
    
    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    if (!video || !canvas) return;

    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convert to blob and send to backend
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append('image', blob, 'face_capture.jpg');

      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/student/mark-attendance', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
          body: formData
        });

        const result = await response.json();

        if (result.success) {
          setResult(result);
          setStep('result');
        } else {
          alert(result.error || 'Recognition failed');
        }
      } catch (error) {
        alert('Recognition failed: ' + error.message);
      } finally {
        setLoading(false);
      }
    }, 'image/jpeg', 0.9);
  };

  const closeCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
  };

  const handleClose = () => {
    closeCamera();
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="attendance-modal">
        {/* Header */}
        <div className="modal-header">
          <h2>
            {step === 'instructions' && 'üì∑ Mark Attendance'}
            {step === 'camera' && 'üéØ Face Recognition'}
            {step === 'result' && '‚úÖ Attendance Marked'}
          </h2>
          <button className="close-btn" onClick={handleClose}>√ó</button>
        </div>

        {/* Instructions Step */}
        {step === 'instructions' && (
          <div className="modal-content">
            <div className="instructions">
              <div className="instruction-item">
                <div className="instruction-icon">1Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Position Yourself</h3>
                  <p>Find a well-lit area and face the camera directly</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">2Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Remove Accessories</h3>
                  <p>Take off hats, sunglasses for better recognition</p>
                </div>
              </div>
              
              <div className="instruction-item">
                <div className="instruction-icon">3Ô∏è‚É£</div>
                <div className="instruction-text">
                  <h3>Stay Still</h3>
                  <p>Keep your face steady while we capture the image</p>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={handleClose}>
                Cancel
              </button>
              <button className="btn primary" onClick={startCamera}>
                Start Camera
              </button>
            </div>
          </div>
        )}

        {/* Camera Step */}
        {step === 'camera' && (
          <div className="modal-content">
            <div className="camera-container">
              <video 
                ref={videoRef} 
                autoPlay 
                playsInline
                className="camera-video"
              />
              <canvas ref={canvasRef} style={{ display: 'none' }} />
              
              <div className="camera-overlay">
                <div className="face-guide"></div>
              </div>
            </div>

            <div className="camera-instructions">
              <p>Position your face within the frame and click capture</p>
            </div>

            <div className="modal-actions">
              <button className="btn secondary" onClick={() => setStep('instructions')}>
                Back
              </button>
              <button 
                className="btn primary capture-btn"
                onClick={captureAndRecognize}
                disabled={loading}
              >
                {loading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    Recognizing...
                  </div>
                ) : (
                  'üì∏ Capture & Recognize'
                )}
              </button>
            </div>
          </div>
        )}

        {/* Result Step */}
        {step === 'result' && result && (
          <div className="modal-content">
            <div className="result-success">
              <div className="success-animation">
                <div className="checkmark">‚úì</div>
              </div>
              
              <h3>Attendance Marked Successfully!</h3>
              
              <div className="attendance-details">
                <div className="detail-item">
                  <span className="label">Student:</span>
                  <span className="value">{result.student.name}</span>
                </div>
                <div className="detail-item">
                  <span className="label">ID:</span>
                  <span className="value">{result.student.student_id}</span>
                </div>
                <div className="detail-item">
                  <span className="label">Confidence:</span>
                  <span className="value confidence">
                    {(result.confidence * 100).toFixed(1)}%
                  </span>
                </div>
                <div className="detail-item">
                  <span className="label">Time:</span>
                  <span className="value">{result.timestamp}</span>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button className="btn primary" onClick={handleClose}>
                Done
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceModal;
